mapper.insert:
INSERT INTO fb_account
	(
	 FB_ACCOUNT_ID
	 , REG_USER_ID
	 , FB_USER_ID
	 , FB_USER_NAME
	 , REG_DATE
	)
	VALUES (
			#{fbAccountId}
			, #{regUserId}
			, #{fbUserId}
			, #{fbUserName}
			, CURRENT_TIMESTAMP()
			)

mapper.update:
UPDATE fb_account
	SET FB_USER_ID		= #{fbUserId}
		, FB_USER_NAME	= #{fbUserName}
	WHERE FB_ACCOUNT_ID = #{fbAccountId}
